mapper.select:
	SELECT COUNT(*) 
      FROM MEMBER_TBL
     WHERE MEM_ID = #{memId}

SELECT COUNT(*)
  FROM MEMBER_TBL
 WHERE EMAIL = #{ email }

SELECT A.LOCATION_CODE
  FROM LOCATION A
 WHERE A.LOCATION_NAME = #{ locationName }

mapper.insert:

		INSERT ALL
		  INTO MEMBER_TBL
		VALUES
		( 
   		  SEQ_MEM_CODE.NEXTVAL
		, #{ email }
		, #{ memPwd }
		, #{ address }
		, #{ postCode }
		, #{ dAddress }
		, #{ phone }
		, 'user'
		, #{ locationCode }
		, #{ nickname }
		, #{ memId }
		, SYSDATE	
		, 'N'	
		)
		INTO USER_TBL 
		VALUES
		( 
		  SEQ_MEM_CODE.CURRVAL
		, #{ user.name }
		, 0
		)
		SELECT * FROM DUAL

mapper.update:
 		UPDATE EMAIL_CODE_TBL 
		   SET MEM_CODE = #{ memCode }       
		 WHERE CODE = #{ code }

 		UPDATE EMAIL_CODE_TBL 
		   SET EMAIL = #{ email }
		 WHERE CODE = #{ code }

	UPDATE EMAIL_CODE_TBL
	   SET EMAIL_CODE = #{ emailCode }
     WHERE EMAIL = #{ email }

mapper.select:
SELECT EMAIL_CODE
  FROM EMAIL_CODE_TBL
 WHERE EMAIL = #{ email }

mapper.update:
