SELECT A.RNUM , A.SD_CODE , A.SD_NAME , A.M_DATE , A.ORDERABLE_STATUS , A.EXPIRY_DATE FROM (SELECT ROWNUM RNUM , B.SD_CODE , B.SD_NAME , B.M_DATE , B.ORDERABLE_STATUS , B.EXPIRY_DATE FROM (SELECT C.SD_CODE , C.SD_NAME , C.M_DATE , C.ORDERABLE_STATUS , C.EXPIRY_DATE FROM PRODUCT_TBL C
C.IS_DELETED = 'N'
AND C.SD_NAME LIKE '%' || #{ searchValue } || '%'
AND C.EXPIRY_DATE BETWEEN #{ eDate } AND #{ eDate2 }
AND C.EXPIRY_DATE <= #{ eDate }
AND C.EXPIRY_DATE >= #{ eDate }
AND C.M_DATE BETWEEN #{ mDate } AND #{ mDate2 }
AND C.M_DATE <= #{ mDate }
AND C.M_DATE >= #{ mDate }
AND C.ORDERABLE_STATUS = 'Y'
AND C.ORDERABLE_STATUS = 'N'
AND C.MEM_CODE = #{ memCode }
ORDER BY C.M_DATE DESC ) B ) A WHERE A.RNUM BETWEEN #{ pagenation.startRow } AND #{ pagenation.endRow }

