AND A.ORDER_ACCEPT_TIME <= #{ mDate }
AND A.ORDER_ACCEPT_TIME >= #{ mDate }
ORDER BY A.ORDER_RUQEUST_TIME DESC ) F ) G WHERE G.RNUM BETWEEN #{ pagination.startRow } AND #{ pagination.endRow }

