SELECT COUNT(*) FROM (SELECT TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD') AS PAYDATE , SUM(TOTAL_PRICE) AS totalPrice FROM ORDER_TBL WHERE STORE_NAME = #{ storeName } AND TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD') = TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD') GROUP BY TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD')) A LEFT JOIN pickupPrice pic ON (A.payDate = pic.pickupDate) LEFT JOIN DelPrice del ON (A.payDate = del.delDate)
A.PAYDATE BETWEEN #{ date1 } AND #{ date2 }
ORDER BY A.PAYDATE DESC

SELECT C.RNUM , C.PAYDATE , C.TOTALPRICE , C.PICKUPPRICE , C.DELPRICE FROM (SELECT ROWNUM RNUM , B.PAYDATE , B.TOTALPRICE , B.PICKUPPRICE , B.DELPRICE FROM (SELECT A.PAYDATE , A.TOTALPRICE , pic.PICKUPPRICE , del.DELPRICE FROM (SELECT TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD') AS PAYDATE , SUM(TOTAL_PRICE) AS totalPrice FROM ORDER_TBL WHERE STORE_NAME = #{ storeName } AND TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD') = TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD') GROUP BY TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM-DD')) A LEFT JOIN pickupPrice pic ON (A.PAYDATE = pic.PICKUPDATE) LEFT JOIN DelPrice del ON (A.PAYDATE = del.DELDATE)
A.PAYDATE BETWEEN #{ date1 } AND #{ date2 }
ORDER BY A.PAYDATE DESC) B ) C WHERE C.RNUM BETWEEN #{ pagination.startRow } AND #{ pagination.endRow }

SELECT C.RNUM , C.PAYDATE , C.TOTALPRICE , C.PICKPRICE , C.DELPRICE FROM (SELECT ROWNUM RNUM , B.PAYDATE , B.TOTALPRICE , B.PICKPRICE , B.DELPRICE FROM (SELECT A.PAYDATE , A.totalPrice , pic.PICKPRICE , del.DELPRICE FROM (SELECT TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM') AS PAYDATE , SUM(TOTAL_PRICE) AS totalPrice FROM ORDER_TBL WHERE STORE_NAME = #{ storeName } AND TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM') = TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM') GROUP BY TO_CHAR(ORDER_COMPLETE_TIME,'YYYY-MM')) A LEFT JOIN MONTH_PICKUP pic ON (A.PAYDATE = pic.PAYDATE) LEFT JOIN MONTH_DEL del ON (A.PAYDATE = del.PAYDATE)
A.PAYDATE BETWEEN #{ date1 } AND #{ date2 }
ORDER BY A.PAYDATE DESC) B ) C WHERE C.RNUM BETWEEN #{ pagination.startRow } AND #{ pagination.endRow }

