mapper.select:
SELECT COUNT(*) FROM COUPON_TBL A JOIN CP_HISTORY_TBL B ON(A.CP_CODE = B.CP_CODE) WHERE B.USE_STATUS = 'N' AND B.MEM_CODE = #{ searchCondition }
AND A.CP_NAME LIKE '%' || #{ searchValue } || '%'
AND SYSDATE <= A.END_DATE

SELECT A.RNUM , A.CP_CODE , A.CP_NAME , A.START_DATE , A.DISCOUNT_WON , A.END_DATE , A.DC_CONDITION , A.STORE_NAME , A.STORE_IMG , A.MEM_CODE FROM (SELECT ROWNUM RNUM , B.CP_CODE , B.CP_NAME , B.START_DATE , B.DISCOUNT_WON , B.END_DATE , B.DC_CONDITION , B.STORE_NAME , B.STORE_IMG , B.MEM_CODE FROM (SELECT C.CP_CODE , C.CP_NAME , C.START_DATE , C.DISCOUNT_WON , C.END_DATE , C.DC_CONDITION , F.STORE_NAME , F.STORE_IMG , F.MEM_CODE FROM COUPON_TBL C JOIN CP_HISTORY_TBL D ON (C.CP_CODE = D.CP_CODE) JOIN COUPON_STORE_TBL E ON (C.CP_CODE = E.CP_CODE) JOIN STORE_TBL F ON (E.MEM_CODE= F.MEM_CODE)
D.USE_STATUS = 'N'
AND SYSDATE <= C.END_DATE
AND C.CP_NAME LIKE '%' || #{ searchValue } || '%'
ORDER BY C.END_DATE ASC )B
WHERE ROWNUM <= #{ endRow }
)A
WHERE A.RNUM >= #{ startRow }

SELECT A.RNUM , A.MEM_CODE , A.TOTAL_PRICE , A.ORDER_CODE , A.ORDER_TYPE , A.TAKE_TIME , A.ADDRESS , A.PHONE , A.STORE_NAME , A.CODE , A.ORDER_CANCLE_TIME , A.ORDER_COMPLETE_TIME , A.ORDER_ACCEPT_TIME , A.STORE_IMG FROM (SELECT ROWNUM RNUM , B.MEM_CODE , B.TOTAL_PRICE , B.ORDER_CODE , B.ORDER_TYPE , B.TAKE_TIME , B.ADDRESS , B.PHONE , B.STORE_NAME , B.CODE , B.ORDER_CANCLE_TIME , B.ORDER_COMPLETE_TIME , B.ORDER_ACCEPT_TIME , B.STORE_IMG FROM (SELECT C.MEM_CODE , C.TOTAL_PRICE , C.ORDER_CODE , C.ORDER_TYPE , C.TAKE_TIME , C.ADDRESS , C.PHONE , C.STORE_NAME , C.CODE , C.ORDER_CANCLE_TIME , C.ORDER_COMPLETE_TIME , C.ORDER_ACCEPT_TIME , F.STORE_IMG FROM ORDER_TBL C JOIN ORDER_INFO_TBL D ON(D.ORDER_CODE = C.ORDER_CODE) JOIN PRODUCT_TBL E ON(E.SD_CODE=D.SD_CODE) JOIN STORE_TBL F ON(E.MEM_CODE=F.MEM_CODE) WHERE C.MEM_CODE = #{ searchCondition }
AND C.STORE_NAME LIKE '%' || #{ searchValue } || '%'
ORDER BY C.ORDER_CODE DESC )B
WHERE ROWNUM <= #{ endRow }
)A
WHERE A.RNUM >= #{ startRow }

SELECT A.RNUM , A.STORE_NAME , A.STORE_IMG , A.STORE_INFO , A.GRADE , A.DEL_COST , A.WORK_TIME , A.MEM_CODE , A.CEO_CODE FROM (SELECT ROWNUM RNUM , B.STORE_NAME , B.STORE_IMG , B.STORE_INFO , B.GRADE , B.DEL_COST , B.WORK_TIME , B.MEM_CODE , B.CEO_CODE FROM (SELECT C.STORE_NAME , C.STORE_IMG , C.STORE_INFO , C.GRADE , C.DEL_COST , C.WORK_TIME , C.MEM_CODE , E.CEO_CODE FROM STORE_TBL C JOIN CEO_TBL D ON(C.MEM_CODE = D.MEM_CODE) JOIN JJIM_TBL E ON(D.MEM_CODE = E.CEO_CODE) WHERE E.MEM_CODE = #{ searchCondition }
AND C.STORE_NAME LIKE '%' || #{ searchValue } || '%'
ORDER BY STORE_NAME ASC )B
WHERE ROWNUM <= #{ endRow }
)A
WHERE A.RNUM >= #{ startRow }

mapper.delete:
DELETE
  FROM JJIM_TBL A
WHERE A.CEO_CODE = #{ storeNo }

mapper.select:
SELECT A.RNUM , A.MEM_CODE , A.ORDER_CODE , A.RV_IMG , A.RV_CONTENT , A.RV_GRADE , A.RV_CODE , A.MEM_ID , A.IS_DELETED , A.ORDER_RUQEUST_TIME FROM (SELECT ROWNUM RNUM , B.MEM_CODE , B.ORDER_CODE , B.RV_IMG , B.RV_CONTENT , B.RV_GRADE , B.RV_CODE , B.MEM_ID , B.IS_DELETED , B.ORDER_RUQEUST_TIME FROM (SELECT C.MEM_CODE , C.ORDER_CODE , C.RV_IMG , C.RV_CONTENT , C.RV_GRADE , C.RV_CODE , C.MEM_ID , C.IS_DELETED , D.ORDER_RUQEUST_TIME FROM REVIEW_TBL C JOIN ORDER_TBL D ON(D.ORDER_CODE = C.ORDER_CODE) WHERE C.MEM_CODE = #{ searchCondition } AND C.IS_DELETED = 'N'
AND D.STORE_NAME = #{ searchValue }
ORDER BY D.ORDER_RUQEUST_TIME DESC )B
WHERE ROWNUM <= #{ endRow }
)A
WHERE A.RNUM >= #{ startRow }

mapper.insert:
INSERT
INTO REVIEW_TBL
(
  MEM_CODE
, ORDER_CODE
, RV_IMG
, RV_CONTENT
, RV_GRADE
, RV_CODE
, MEM_ID
, IS_DELETED
)
VALUES
(
  #{ memCode }
, #{ orderCode }
, #{ img }
, #{ content }
, #{ grade }
, SEQ_REVIEW.NEXTVAL
, #{ memId }
, 'N'
)

mapper.update:

       		SELECT A.STORE_CODE FROM ORDER_TBL A WHERE A.ORDER_CODE = #{ orderCode }

		UPDATE STORE_TBL
		   SET GRADE = #{ grade }
		 WHERE MEM_CODE = #{ memCode }
